import Content from './Content.js';

function createTitle(title) {
  return {__html: title};
}

function Post(props) {
  const post = props.post;
  const contents = post.contents;

  return (
    <div className="border border-3 margin-small padding-small">
      <article className="article">
        <h2 className="article-title text-center" dangerouslySetInnerHTML={createTitle(post.title)}/>
        <p className="text-lead text-center">{post.subtitle}</p>        

        {contents.map((content, i) =>
          <Content key={i} content={content} />
        )}

      </article>
    </div>
  );
}

export default Post;
